import customProperty from 'fela-plugin-custom-property';
import reduce from 'lodash/reduce';

var placeholderPrefixes = ['::-webkit-input-placeholder', '::-moz-placeholder', ':-ms-input-placeholder', ':-moz-placeholder', '::placeholder'];

export default function placeholderPrefixer() {
  return customProperty({
    '::placeholder': function placeholder(value) {
      return reduce(placeholderPrefixes, function (style, prefix) {
        style[prefix] = value;
        return style;
      }, {});
    }
  });
}